################################################################################################################################################################
##R script to count combination of single nucleotide variant, indel and structural variant calls affecting genes of interest and compare frequency vs controls##
################################################################################################################################################################

##Utilises tables of variants and counts generated by scripts for SNV/indel analysis and SV analysis

############################################################################################################
##Following commands used to launch script according to phenotypic subgroup:
#Rscript GOF_SNV_SV_combination.R 1FROM10 Melanoma Lung Bladder GIST Kidney Thyroidmedullary Phaeochromocytoma Paraganglioma Connectivetissuesofttissuesarcoma Haemmyeloid
#Rscript GOF_SNV_SV_combination.R ALL XXX XXX XXX XXX XXX XXX XXX XXX XXX XXX
############################################################################################################

args = commandArgs(trailingOnly=TRUE)

##Read in tables of variants and combine
SNV <- read.csv("GOF_variants_per_gene_table_all_combined.csv")
SNV <- data.frame(SNV$gene, SNV$indv_per_gene_count_cases_hets, SNV$indv_per_gene_count_BRIDGES_hets, SNV$indv_per_gene_count_cases_homs, SNV$indv_per_gene_count_BRIDGES_homs, SNV$tumour_col)
colnames(SNV) <- c("gene", "cases_count_hets", "controls_count_hets", "cases_count_homs", "controls_count_homs", "tumour_col")

SV <- read.csv("collated_SV_table_all.csv")
SV <- data.frame(SV$gene, SV$cases_count_hets, SV$controls_count_hets, SV$cases_count_homs, SV$controls_count_homs, SV$tumour_col)
colnames(SV) <- c("gene", "cases_count_hets", "controls_count_hets", "cases_count_homs", "controls_count_homs", "tumour_col")

library(data.table)
SNV_SV_table <- rbindlist(list(SV, SNV), fill=TRUE)
SNV_SV_table$tumour_col <- as.vector(SNV_SV_table$tumour_col)
SNV_SV_table$tumour_col[which(SNV_SV_table$tumour_col == "ALL")] <- "ALLXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX"

##Read in gene list
gene_list <- read.delim("GOF_gene_list.txt", header = F)

##Subset table to only genes on gene list
SNV_SV_table<- SNV_SV_table[which(SNV_SV_table$gene %in% gene_list$V1),]
SNV_SV_table$gene <- as.character(SNV_SV_table$gene)

##Subset table according to phenotypic subgroup
SNV_SV_table <- SNV_SV_table[which(SNV_SV_table$tumour_col == paste(args[1],args[2],args[3],args[4],args[5],args[6],args[7],args[8],args[9],args[10],args[11],sep = "")),]
SNV_SV_table$tumour_col <- gsub("X", "", SNV_SV_table$tumour_col)

##Produce sums of SV and SNV counts for each gene
cases_count_hets <- unlist(lapply(gene_list$V1, function(x) sum(SNV_SV_table$cases_count_hets[which(SNV_SV_table$gene == x)])))
controls_count_hets <- unlist(lapply(gene_list$V1, function(x) sum(SNV_SV_table$controls_count_hets[which(SNV_SV_table$gene == x)])))
cases_count_homs <- unlist(lapply(gene_list$V1, function(x) sum(SNV_SV_table$cases_count_homs[which(SNV_SV_table$gene == x)])))
controls_count_homs <- unlist(lapply(gene_list$V1, function(x) sum(SNV_SV_table$controls_count_homs[which(SNV_SV_table$gene == x)])))
cases_count <- cases_count_hets + cases_count_homs
controls_count <- controls_count_hets + controls_count_homs
cases_controls_count <- data.frame(gene_list, cases_count, controls_count, cases_count_hets, controls_count_hets, cases_count_homs, controls_count_homs)
colnames(cases_controls_count)[1] <- "gene"

